import React, { useEffect } from 'react'
import { useForm } from "react-hook-form";
import { nanoid } from "nanoid";
export default function TaskHookForm(props) {
  let {kisiler,submitFn}=props
  console.log(kisiler,submitFn);
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    mode:onchange,
    defaultValues: {
      title: "",
      description: "",
      people:[]

    },
  });
  useEffect(() => {
    console.log(register.title)
  
  }, [register]); 
  
  function handleSubmit1(formState) {
  
    submitFn({
      ...formState,
      id: nanoid(5),
      status: "yapılacak",
    });
    
  }
  
  return (
    <form className="taskForm"  onSubmit={handleSubmit(handleSubmit1)}>

      <div className="form-line">
        <label className="input-label" htmlFor="title">
          Başlık
        </label>
        <input
          className="input-text"
          id="title"
          type="text"
          {...register("title", { required: "Task başlığı yazmalısınız",
            min: (3,"Task başlığı en az 3 karakter olmalı")
          })}
          />
        <p className="input-error">{console.log(errors.title) && errors.title.message}</p>
      </div>

      <div className="form-line">
        <label className="input-label" htmlFor="description">
          Açıklama
        </label>
        <textarea
          className="input-textarea"
          rows="3"
          id="description"
          {...register("title", { required: "Task açıklaması yazmalısınız",
            min: (10,"Task açıklaması en az 10 karakter olmalı")
          })}
        ></textarea>
        <p className="input-error">{errors.description?.message  }</p>
      </div>

      <div className="form-line">
        <label className="input-label">İnsanlar</label>
        <div>
          {kisiler.map((p) => (
            <label className="input-checkbox" key={p}>
              <input
                type="checkbox"
                value={p}
                {...register("people", { required: "Task açıklaması yazmalısınız",
                min: (1,"Lütfen en az bir kişi seçin"),
                max: (3, "En fazla 3 kişi seçebilirsiniz")
              })}
              />
              {p}
            </label>
          ))}
        </div>
        <p className="input-error">{errors.people?.message}</p>
      </div>

      <div className="form-line">
        <button
          className="submit-button"
          type="submit"
          disabled={!isValid}
        >
          Kaydet
        </button>
      </div>
    </form>
  );
};
